package strings;

public class v4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str = "www.viramchoksi.co.in";
		
		//find the specific charcter's index value
		System.out.println(str.indexOf(".")); //first index value
		System.out.println(str.indexOf("." , 4)); //"." after index value 4
		System.out.println(str.lastIndexOf(".")); //last index value
		

	}

}
